function numeroPorExtenso(numero) {
    const unidades = ["", "um", "dois", "três", "quatro", "cinco", "seis", "sete", "oito", "nove"];
    const especiais = ["dez", "onze", "doze", "treze", "quatorze", "quinze", "dezesseis", "dezessete", "dezoito", "dezenove"];
    const dezenas = ["", "", "vinte", "trinta", "quarenta", "cinquenta", "sessenta", "setenta", "oitenta", "noventa"];
    const centenas = ["", "cento", "duzentos", "trezentos", "quatrocentos", "quinhentos", "seiscentos", "setecentos", "oitocentos", "novecentos"];

    if (numero === 0) return "zero";

    let extenso = "";

    if (numero >= 1000) {
        const milhar = Math.floor(numero / 1000);
        extenso += (milhar === 1 ? "mil" : numeroPorExtenso(milhar) + " mil");
        numero %= 1000;
    }

    if (numero >= 100) {
        const centena = Math.floor(numero / 100);
        extenso += (extenso ? " " : "") + (centena === 1 && numero % 100 === 0 ? "cem" : centenas[centena]);
        numero %= 100;
    }

    if (numero >= 20) {
        const dezena = Math.floor(numero / 10);
        extenso += (extenso ? " e " : "") + dezenas[dezena];
        numero %= 10;
    }

    if (numero >= 10) {
        extenso += (extenso ? " e " : "") + especiais[numero - 10];
        return extenso;
    }

    if (numero > 0) {
        extenso += (extenso ? " e " : "") + unidades[numero];
    }

    return extenso;
}

function valorPorExtenso(valor) {
    const reais = Math.floor(valor);
    const centavos = Math.round((valor - reais) * 100);

    let extenso = numeroPorExtenso(reais) + (reais === 1 ? " real" : " reais");

    if (centavos > 0) {
        extenso += " e " + numeroPorExtenso(centavos) + (centavos === 1 ? " centavo" : " centavos");
    }

    return extenso;
}

